import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'; // Cambiamos 'Switch' por 'Route'

import ProductList from './components/ProductList';
import Cart from './components/Cart';
import './App.css';

const App = () => {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (product) => {
    setCartItems([...cartItems, product]);
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Productos</Link>
            </li>
            <li>
              <Link to="/cart">Carrito ({cartItems.length})</Link>
            </li>
          </ul>
        </nav>

        <Route exact path="/" component={() => <ProductList addToCart={addToCart} />} />
        <Route path="/cart" component={() => <Cart cartItems={cartItems} />} />
      </div>
    </Router>
  );
};

export default App;
