// Cart.jsx

import React from 'react';
import './Cart.css'; // Importamos el archivo de estilos específico para Cart

const Cart = ({ cartItems, removeFromCart }) => {
  const getTotalPrice = () => {
    return cartItems.reduce((total, item) => total + item.price, 0);
  };

  return (
    <div className="cart-container"> {/* Agregamos una clase al contenedor de los elementos del carrito */}
      <h2>Carrito</h2>
      {cartItems.map(item => (
        <div key={item.id} className="cart-item"> {/* Agregamos una clase para cada elemento del carrito */}
          <img src={item.image} alt={item.title} />
          <div className="cart-item-info"> {/* Agregamos una clase para la información del elemento del carrito */}
            <h3>{item.title}</h3>
            <p>{item.price}$</p>
          </div>
          <div className="cart-item-remove" onClick={() => removeFromCart(item)}>X</div> {/* Añadimos un botón para eliminar el elemento del carrito */}
        </div>
      ))}
      <div className="total">Total: {getTotalPrice()}$</div> {/* Mostramos el total */}
    </div>
  );
};

export default Cart;
